import React, { useState } from 'react';

const Todo = () => {
    const [name, setName] = useState('');
    const [model, setModel] = useState('');
    const [cars, setCars] = useState([
        { id: 1, name: 'Toyota', model: 1970 },
        { id: 2, name: 'Honda', model: 1954 },
        { id: 3, name: 'BMW', model: 1982 },
        { id: 4, name: 'Mercedes-Benz', model: 1963 }
    ]);

    function handleNameChange(event) {
        setName(event.target.value);
    }

    function handleModelChange(event) {
        setModel(event.target.value);
    }

    function addItem() {
        if (name.trim() === '' || model.trim() === '') {
            alert('Please enter both car name and model');
        } else {
            const newCar = {
                id: Date.now(), // Generate a unique ID
                name: name,
                model: model
            };
            setCars([...cars, newCar]);
            setName('');
            setModel('');
        }
    }

    function removeCar(id) {
        const updatedCarsArray = cars.filter((car) => {
            return car.id !== id;
        });
        setCars(updatedCarsArray);
    }

    return (
        <div>
            <input type="text" value={name} placeholder='Enter a car name' onChange={handleNameChange} />
            <br />
            <input type="text" value={model} placeholder='Enter Model name' onChange={handleModelChange} />
            <button className='btn btn-primary' onClick={addItem}>Add</button>
            {
                cars.map((car) => (
                    <div key={car.id}>
                        <p><b>Car Name : </b>{car.name}</p>
                        <p><b>Model Name : </b>{car.model}</p>
                        <button className='btn btn-danger' onClick={() => { removeCar(car.id) }}>Remove</button>
                        <hr />
                    </div>
                ))
            }
        </div>
    );
}

export default Todo;
