import React, { useState } from "react";

const UseState = () => {
    let counter = 0;
    const inc = () =>{
        counter = counter + 1;
        
    }
    return (
        <>
            {counter}
            <br />
            <button onClick={inc}>Increment</button>
        </>
    )
}
export default UseState;
