import React, { useState } from 'react'

const Todo = () => {
    const [state, setstate] = useState("")
    const [cars, setCars] = useState([
        {
            name: 'Toyota',
            model: 1970
        },
        {
            name: 'Honda',
            model: 1
        },
        {
            name: 'BMW',
            model: 1970
        },
        {
            name: 'Mercedes-Benz',
            model: 1970
        },
        {
            name: 'Ford',
            model: 1970
        },
        {
            name: 'Chevrolet',
            model: 1970
        },
        {
            name: 'Audi',
            model: 1970
        },
        {
            name: 'Nissan',
            model: 1970
        },
        {
            name: 'Volkswagen',
            model: 1970
        },
        {
            name: 'Tesla',
            model: 1970
        },
    ])
    function myfun(event) {
        setstate(event.target.value)
    }
    function addItem() {
        let x = { name: state }
        setCars([...cars, x]);
        setstate("")
    }
    return (
        <div>
            <input type="text" value={state} placeholder='Enter a car name' onChange={myfun} />
            <button className='btn btn-primary' onClick={addItem}>Add</button>
            {
                cars.map((n) => (
                    <div>
                        <p><b>Car Name : </b>{n.name}</p>
                    </div>
                ))
            }
        </div>
    )
}

export default Todo
