import React, { useState } from 'react'

const UseReducer = () => {
    const [Counter , setCounter] = useState(0);
    const [text , settext] = useState(true);
  return (
    <div>
      <h1>{Counter}</h1><br />
      <button onClick={()=>{
        setCounter(Counter+1)
        settext
      }}>Click me</button><br />
      <h1>{text}</h1>
    </div>
  )
}

export default UseReducer
