const UseStateTest = () => {

  const [Obj,setObj] = useState({
    name:"",
    clas:"",
    group:"",
  });
  var name;
  var value;
  const nm = (e) =>{
    name=e.target.name
    value=e.target.value
  setObj({...Obj,[name]:value})
  console.log(Obj);
  }

  const clr = () =>{
    // setName('')
