import React, { useState } from 'react'
import './UseStateTest.css'
const UseStateTest = () => {
  const [Name,setName] = useState(' ');
  const [Clas,setClas] = useState('class');
  const [Group,setGroup] = useState('group');
  const nm = (e) =>{
    setName(e.target.value)
  }
  const cl = (e) =>{
    setClas(e.target.value)
  }
  const gr = (e) =>{
    setGroup(e.target.value)
  }
  const clr = () =>{
    setName('')
    setClas('')
    setGroup('')

  }
  
  return (
    <div>
      <span>Name : <input type="text" onChange={nm}/></span>
      <span>Class : <input type="text" onChange={cl}/></span>
      <span>Group : <input type="text" onChange={gr}/></span>
      <button onClick={clr}>Click me to clear</button>
      <h1>{Name}</h1>
      <h1>{Clas}</h1>
      <h1>{Group}</h1>
    </div>
  )
}

export default UseStateTest
