import React, { useState } from "react";

const UseState = () => {
 const [value,setValue] = useState("hi")
    const sv = (e) =>{
        setValue(e.target.value)
        console.log(value);
    }
    return (
        <>
            <input type="text" name="" id="" onChange={sv}/>
            <h1>{value}</h1>
        </>
    )
}
export default UseState;
