import React, { useState } from 'react'

const MainPage2 = () => {
    const [counter, setcounter] = useState(0)
    function increment() {
        //Not greate
        setcounter(counter+1)
    }
    function decrement() {
        // Not less than 0
        setcounter(counter-1)
    }
    return (
        <div>
            <h1>Counter APP</h1>
            Count : {counter}
            <br />
            <button onClick={increment}>INCREMENT</button>
            <button onClick={decrement}>DECREMENT</button>
        </div>
    )
}

export default MainPage2
