import React, { useState } from 'react'

const ColorFlipper = () => {
    const [r, setr] = useState(0)
    const [g, setg] = useState(0)
    const [b, setb] = useState(0)
    function changeVal() {
        let nr = Math.floor(Math.random() * 256);
        let ng = Math.floor(Math.random() * 256);
        let nb = Math.floor(Math.random() * 256);
        setr(nr)
        setg(ng)
        setb(nb)
    }
    return (
        <div className='defineColor'>
            <div className="textDiv">
                <h2> Red &nbsp;&nbsp;&nbsp;&nbsp;: {r} </h2>
                <h2> Blue &nbsp;&nbsp;&nbsp;: {b} </h2>
                <h2> Green&nbsp;&nbsp;: {g} </h2>
                <button onClick={changeVal} className='btn btn-p'>Change values</button>
            </div>
        </div>
    )
}

export default ColorFlipper
